    private void BsaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BsaveActionPerformed

        String student = BstudentID.getText();
        String name= BName.getText();
        String course = BCourse.getText();
        String section = BSection.getText();
        String date= BDate.getText();
        String status =BcmbStat.getSelectedItem().toString();
        try{
            conn=Connectionr.getConnection();
            ps=conn.prepareStatement("insert into SECTION_B (ID, NAME, COURSE, SECTION, DATE, STATUS)values (?,?,?,?,?,?)");

            ps.setString(1,student);
            ps.setString(2,name);
            ps.setString(3,course);
            ps.setString(4,section);
            ps.setString(5,date);
            ps.setString(6,status);
            int ok = JOptionPane.showConfirmDialog(this, "Confirm to Save", "Are you sure..", JOptionPane.YES_NO_OPTION);
            if (ok==0){
                ps.execute();
                JOptionPane.showMessageDialog(this,"Successful", "DONE", 1);
            }
        }catch (Exception ex) {
            System.out.println(""+ex);
        }
    }//GEN-LAST:event_BsaveActionPerformed

