    private void btnSearchBActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSearchBActionPerformed
        try{

            String sql;
            sql = "select * from SECTION_A where NAME = ?";

            ps=conn.prepareStatement(sql);
            ps.setString(1 , Bsearch.getText());

            rs=ps.executeQuery();
            if(rs.next()){
                String add1=rs.getString("ID");
                BstudentID.setText(add1);
                String add2=rs.getString("NAME");
                BName.setText(add2);
                String add3=rs.getString("COURSE");
                BCourse.setText(add3);
                String add4=rs.getString("SECTION");
                BSection.setText(add4);

            }
            else{ JOptionPane.showMessageDialog(this,"NO MATCH DATA","ERROR",JOptionPane.ERROR_MESSAGE);
            }
        }
        catch (Exception e){
            JOptionPane.showMessageDialog(null,e);
        }
    }//GEN-LAST:event_btnSearchBActionPerformed

